mapper.sql:
		FROM product as a
		WHERE 1=1

		AND a.prdtUseNy = #{shUseNy}
        AND a.prdtDelNy = #{shDelNy}


					AND a.mealRegDateTime BETWEEN #{shDateStart} AND #{shDateEnd}
					AND a.mealModDateTime BETWEEN #{shDateStart} AND #{shDateEnd}



            AND a.prdtSeq = #{shValue}
            AND a.prdtName LIKE CONCAT('%',#{shValue},'%')



	        AND a.prdtName = #{shOption1}


		ORDER BY a.prdtSeq DESC

mapper.insert:
    INSERT INTO product (					
		prdtName
		,prdtCateCd
		,prdtStatus
		,prdtPrice
		,prdtUseNy
		,prdtDelNy
		,prdtRegDate
		,prdtModDate
		,prdtDesc
		,abv
		,ibu
		,volume
		,countryCode
		,brewery
    ) values (			
        #{prdtName}
        ,#{prdtCateCd}
        ,#{prdtStatus}
        ,#{prdtPrice}
        ,#{prdtUseNy}
        ,#{prdtDelNy}
        ,CURRENT_TIMESTAMP
        ,CURRENT_TIMESTAMP
        ,#{prdtDesc}
        ,#{abv}
        ,#{ibu}
        ,#{volume}
        ,#{countryCode}
        ,#{brewery}
    )

        SELECT last_insert_id()

mapper.update:
update product set
	prdtName = #{prdtName}
	,prdtStatus = #{prdtStatus}
	,prdtPrice = #{prdtPrice}
	,prdtCateCd = #{prdtCateCd}
	,prdtUseNy = #{prdtUseNy}
	,prdtDelNy = #{prdtDelNy}
	,prdtModDate = CURRENT_TIMESTAMP
where
	prdtSeq = #{prdtSeq}
